<?php/** * Nikiforov * logger 2021 */namespace vov1zzz\Logger;class FileLogger extends Logger implements LoggerInterface{    public function save($message = null): bool    {        if($message) {            if($this->config['console']) echo $message."\n";            return file_put_contents($this->config['file'], $message, FILE_APPEND);        } else if($this->prepared) {            if($this->config['console']) echo $this->prepared."\n";            $message = $this->prepared;            $this->prepared = "\n";            return file_put_contents($this->config['file'], $message, FILE_APPEND);        }    }}