<?php/** * Nikiforov * logger 2021 */namespace vov1zzz\Logger;abstract class Logger{    public $config;    public $prepared = "\n";    public function __construct($config)    {        $this->config = $config;    }    public function getConfig() {        return $this->config;    }    public function setConfig($config) {        $this->config = $config;    }    public function prepare($text) {        $this->prepared .= $text."\n";        return $this;    }}